<?phpclass Dice {	/*	$dice is expected to be in form of xdy,	where x is the number of dice rolled,	d is the terminology for "dice",	and y is the specified die (usually 6)	*/	public static function roll($dice) {		$split = explode('d', $dice);		$rolls = intval($split[0]);		$die = intval($split[1]);		$result = 0;		for ($i = 0; $i < $rolls; $i++) {			$result += mt_rand(1, $die);		}		return $result;	}}?>